/**
 * 腾讯云 cos 上传
 * https://cloud.tencent.com/document/product/436/11459
 */
import COS from 'cos-js-sdk-v5';
import { uuid, getDateFormatString, formatApiPath } from './utils';
import type { Config, Creator, Credentials, UploadParams, UploadResult } from './index';

const SLICE_SIZE = 1024 * 1024 * 1024;
const MAX_TRY_TIMES = 2; // 失败后尝试请求次数

export function create(config: Required<Config>): Creator {
  
  let credentials: Credentials;
  const auth = config.auth;

  let shareCOS: COS | null = null;
  let shareCOSPromise: Promise<COS> | null = null;

  function createCOS (): Promise<COS> {
    if(!shareCOSPromise) {
      shareCOSPromise =  new Promise((resolve, reject) => {
        auth().then((data) => {
          credentials = data;
          const { id: TmpSecretId, key: TmpSecretKey, token: SecurityToken, startTime, expiredTime } = data;
          const cos = new COS({
            // eslint-disable-next-line @typescript-eslint/explicit-function-return-type
            getAuthorization: (_options, callback) => {
              callback({
                TmpSecretId,
                TmpSecretKey,
                SecurityToken,
                // 建议返回服务器时间作为签名的开始时间，避免用户浏览器本地时间偏差过大导致签名错误
                StartTime: startTime, 
                ExpiredTime: expiredTime, 
            });
            }
          });
          resolve(cos);
        })
        .catch((err) => {
          console.log('err', err);
          reject(err);
        });
      }) 
    }
    return shareCOSPromise;
  }
  /**
   * 重置 shareCOS 引用
   */
  function resetCOS(): void {
    shareCOS = null;
    shareCOSPromise = null;
  }
  /**
   * 实现腾讯云上传
   * @returns
   */
  async function upload(params: UploadParams, tryTimes?: number): Promise<UploadResult> {
    const paramsWithDefault = {
      pathPrefix: '',
      datePrefix: true,
      rename: true,
      ...params,
    };
    if(!shareCOS) {
      shareCOS = await createCOS();
    }
    const {region, bucketName, bucketContentPath, cdnUrl} = credentials;

    // 处理上传文件名
    // let fileKey = encodeURIComponent(paramsWithDefault.file.name);
    let fileKey = paramsWithDefault.file.name; // cos内部已经encode了，重复encode会导致上传中文文件名不可用
    if (paramsWithDefault.rename) {
      const extName = fileKey.lastIndexOf('.') !== -1 ? fileKey.split('.').pop() : '';
      fileKey = uuid() + (extName ? `.${extName}` : '');
    }
    if (paramsWithDefault.datePrefix) {
      const now = new Date();
      fileKey = getDateFormatString(now) + '/' + fileKey;
    }
    if (paramsWithDefault.pathPrefix) {
      fileKey = formatApiPath(paramsWithDefault.pathPrefix) + '/' + fileKey;
    }
    fileKey = formatApiPath(bucketContentPath) + '/' + fileKey;

    let result: any = null;
    try {
      result = await new Promise((resolve, reject) => {
        (shareCOS as COS).uploadFile({
          Bucket: bucketName,              // 填入您自己的存储桶，必须字段 
          Region: region,               // 存储桶所在地域，必须字段
          Key: fileKey,                     // 存储在桶里的对象键（例如1.jpg，a/b/test.txt），必须字段
          SliceSize: SLICE_SIZE,            // 触发分块上传的阈值，默认1GB; 超过1GB使用分块上传，非必须
          Body: params.file, 
        }, (err: COS.CosError, data: COS.UploadFileResult) => {
          if(data) resolve(data);
          if(err) reject(err.error);
        });
      })
    } catch (err) {
      console.error('There was an error uploading your file: ', err);
      if (typeof tryTimes === 'undefined' || tryTimes < MAX_TRY_TIMES) {
        resetCOS();
        result = await upload(params, typeof tryTimes === 'number' ? tryTimes + 1 : 2);
      }
    }
    if (result) {
      let url = '';
      if(cdnUrl) {
        url = formatApiPath(cdnUrl) + '/' + fileKey;
      }else {
        url = 'https://' + result.Location; 
      }
      // 临时获取md5方案： 不触发分块上传取ETag，触发后置为null
      const md5 =  params.file.size > SLICE_SIZE ? null : JSON.parse(result.ETag);

      return {url, md5}; 
    }
    return null;
  }

  return {
    upload
  };
}
